story.text:
strict graph {
  rankdir=TB
  node [shape=box style="filled,rounded"]

  subgraph cluster_macos {
    label="MacOS"
    labeljust=l

    "port :80"

    subgraph cluster_docker {
      label="k3s container"
      labeljust=l

      ingress -- service

      subgraph cluster_deployment {
        label="wiki-deployment"
        labeljust=l

        farm -- {
          "volume .wiki"
          "volume fedwiki"
        }
      }

      service -- farm

      "container /macos/.wiki-k8s" 
      "container /macos/fedwiki" 
    }

    "port :80" -- ingress
    "host ~/.wiki-k8s"
    "host ~/workspace/fedwiki"
  }

  {
    "volume .wiki" -- "container /macos/.wiki-k8s" -- "host ~/.wiki-k8s"
  }

  {
    "volume fedwiki" -- "container /macos/fedwiki" -- "host ~/workspace/fedwiki"
  }

}

